import React, { useEffect, useRef } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import { Doughnut } from 'react-chartjs-2';
import Chart from 'chart.js/auto';
import { saveAs } from 'file-saver';
import html2pdf from 'html2pdf.js';
import ReactDOM from 'react-dom';

const ExpenseList = ({ expenses }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    // Update the chart whenever expenses change
    const ctx = canvasRef.current?.getContext('2d');
    const existingChart = Chart.getChart(ctx);

    if (existingChart) {
      existingChart.destroy();
    }

    new Chart(ctx, {
      type: 'pie',
      data: chartData,
      options: {
        maintainAspectRatio: false, // Make the chart smaller
      },
    });
  }, [expenses]);

  // Calculate expenses by category
  const expensesByCategory = {};
  expenses.forEach((expense) => {
    if (!expensesByCategory[expense.category]) {
      expensesByCategory[expense.category] = [];
    }
    expensesByCategory[expense.category].push(expense);
  });

  // Prepare data for the pie chart
  const chartData = {
    labels: Object.keys(expensesByCategory),
    datasets: [
      {
        data: Object.values(expensesByCategory).map(
          (categoryExpenses) =>
            categoryExpenses.reduce((total, expense) => total + expense.amount, 0)
        ),
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
          'rgba(75, 192, 192, 0.6)',
          'rgba(153, 102, 255, 0.6)',
          'rgba(255, 159, 64, 0.6)',
          // Add more colors as needed
        ],
      },
    ],
  };

  const downloadPDF = () => {
    const content = (
      <div>
        <h2>Expense Summary</h2>
        <Tabs>
          <TabList>
            {Object.keys(expensesByCategory).map((category) => (
              <Tab key={category}>{category}</Tab>
            ))}
          </TabList>
          {Object.keys(expensesByCategory).map((category) => (
            <TabPanel key={category}>
              <h3>{category} Expenses</h3>
              <ul>
                {expensesByCategory[category].map((expense) => (
                  <li key={expense.id}>
                    {expense.description}: ₹{expense.amount.toFixed(2)} - Date: {expense.date}
                  </li>
                ))}
              </ul>
            </TabPanel>
          ))}
        </Tabs>
        <div style={{ marginTop: '20px' }}>
          <h2>Expense Distribution</h2>
          <canvas ref={canvasRef} width="400" height="400" style={{ maxWidth: '100%' }} />
        </div>
      </div>
    );

    const pdfOptions = {
      margin: 10,
      filename: 'expenses.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' },
    };

    const element = React.cloneElement(content, {
      ref: (pdfContent) => {
        html2pdf(pdfContent, pdfOptions);
      },
    });

    // Render the content to a temporary container before creating the PDF
    const tempContainer = document.createElement('div');
    ReactDOM.render(element, tempContainer, () => {
      document.body.appendChild(tempContainer);

      // Clean up the temporary container after rendering PDF
      setTimeout(() => {
        document.body.removeChild(tempContainer);
      }, 1000); // Adjust the delay if needed
    });
  };

  return (
    <div>
      <h2>Expense Summary</h2>
      <Tabs>
        <TabList>
          {Object.keys(expensesByCategory).map((category) => (
            <Tab key={category}>{category}</Tab>
          ))}
        </TabList>
        {Object.keys(expensesByCategory).map((category) => (
          <TabPanel key={category}>
            <h3>{category} Expenses</h3>
            <ul>
              {expensesByCategory[category].map((expense) => (
                <li key={expense.id}>
                  {expense.description}: ₹{expense.amount.toFixed(2)} - Date: {expense.date}
                </li>
              ))}
            </ul>
          </TabPanel>
        ))}
      </Tabs>
      <div style={{ marginTop: '20px' }}>
        <h2>Expense Distribution</h2>
        <canvas ref={canvasRef} width="400" height="400" style={{ maxWidth: '100%' }} />
      </div>
      <div style={{ marginTop: '20px' }}>
        <button onClick={downloadPDF}>Download as PDF</button>
      </div>
    </div>
  );
};

export default ExpenseList;
